// https://leetcode.com/problems/3sum/
/**
 * @param {number[]} nums
 * @return {number[][]}
 */
var threeSum = function(nums) {
    let startTime = Date.now();
    console.log('--------------------------');
    console.log('--Parameters--------------');
    //console.log(`nums    :  ${nums}`);

    // Pick the algorithm
    console.log('--Logs--------------------');
    let result = bruteForce(nums);
    
    let endTime = Date.now();
    console.log('--Stats-------------------');
    console.log('Memory    : ', process.memoryUsage().heapUsed);
    console.log('Total time: ', endTime - startTime);

    console.log('--Result------------------');
    console.log(result);
    console.log('--------------------------');

    return result;
};

// attempt1: loop through the array, and check all combinations
let bruteForce = (nums) => {
    // if there are less than 3 items, then return []
    if (nums.length<3) {
        return [];
    }

    let result = [];

    // sort the array (has to explicitly compare numbers)
    nums.sort((a,b) => a-b);

    for (let i=0; i<nums.length; i++) {
        //console.log(`  i: ${i}, ${nums[i]}`);
        // if the number is > 0, then there's no need to search i-loop further
        if (nums[i] > 0) {
            //console.log(`  breaking i-loop ${i} because ${nums[i]} > 0`);
            break;
        }

        for (let j=i+1; j<nums.length; j++) {
            //console.log(`    j: ${j}, ${nums[j]}`);

            // if the sum of first 2 numbers is > 0, then there's no need to search j-loop further
            if (nums[i] + nums[j] > 0) {
                //console.log(`    breaking j-loop ${j} because ${nums[i]} + ${nums[j]} > 0`);
                break;
            }

            for (let k=j+1; k<nums.length; k++) {
                //console.log(`      k: ${k}, ${nums[k]}`);

                let currentSum = [nums[i], nums[j], nums[k]];

                // if the sum of first 2 numbers is > 0, then there's no need to search k-loop further
                if (currentSum > 0) {
                    //console.log(`    breaking j-loop ${j} because ${nums[i]} + ${nums[j]} > 0`);
                    break;
                }
                
                if (nums[i] + nums[j] + nums[k] == 0) {
                    if (!contains3(result, currentSum)) {
                        //console.log(`found a set: ${i}, ${j}, ${k}`);
                        result.push(currentSum);
                        break;
                    }
                }
                
            }
        }
    }

    return result;
};

let contains3 = (array, item) => {
    for (let i=0; i<array.length; i++) {
        if (   array[i][0] == item[0]
            && array[i][1] == item[1]
            && array[i][2] == item[2]) {
                return true;
            }
    }

    return false;
};



